mapper.select:
SELECT ID customerId, name, phone, SID storeId FROM customer_list
name LIKE CONCAT('%', #{searchWord}, '%') OR phone LIKE CONCAT('%', #{searchWord}, '%')
SID = ${storeId}
LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) FROM customer_list
name LIKE CONCAT('%', #{searchWord}, '%') OR phone LIKE CONCAT('%', #{searchWord}, '%')
SID = ${storeId}
