mapper.select:
SELECT l.FID filmId, l.title title, l.category category, l.price price, f.rental_duration duration, l.length length, l.rating rating FROM film_list l INNER JOIN film f ON l.FID = f.film_id
AND (l.title LIKE CONCAT('%',#{searchWord},'%') OR l.actors LIKE CONCAT('%',#{searchWord},'%'))
AND l.title LIKE CONCAT('%',#{searchWord},'%')
AND l.actors LIKE CONCAT('%',#{searchWord},'%')
AND l.category = #{categoryName}
AND l.price = #{price}
AND f.rental_duration = #{duration}
AND l.rating = #{rating}
LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*)
FROM film_list l
INNER JOIN film f
ON l.FID = f.film_id


		AND (l.title LIKE CONCAT('%',#{searchWord},'%') OR l.actors LIKE CONCAT('%',#{searchWord},'%'))


		AND l.title LIKE CONCAT('%',#{searchWord},'%')


		AND l.actors LIKE CONCAT('%',#{searchWord},'%')


		AND l.category = #{categoryName}


		AND l.price = #{price}


		AND f.rental_duration = #{duration}


		AND l.rating = #{rating}
